import React, { useState } from "react";
import axios from "axios";
import { useHistory } from "react-router-dom";

const Register = () => {
  const [name, setName] = useState("");
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [confPassword, setConfPassword] = useState("");
  const [msg, setMsg] = useState("");
  const history = useHistory();

  const handleRegister = async (e) => {
    e.preventDefault();
    try {
      await axios.post("http://localhost:5000/users", {
        name: name,
        email: email,
        password: password,
        confPassword: confPassword,
      });
      history.push("/");
    } catch (error) {
      if (error.response) {
        setMsg(error.response.data.msg);
      }
    }
  };

  const className = {
    outerBox:
      "bg-gray-200 min-h-screen min-w-full flex items-center justify-center",
    box: "container mx-auto flex justify-center",
    innerBox: "w-96",
    form: "bg-white shadow-md rounded px-8 pt-6 pb-8 mb-4",
    warnText: "text-center text-red-500 mb-4",
    formSpacing: "mb-4",
    formLabel: "block text-gray-700 text-sm font-bold mb-2",
    formInput:
      "shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline",
    submitBox: "flex mt-2 items-center justify-between",
    formSubmit:
      "bg-green-500 hover:bg-green-700 text-white font-bold py-2 px-4 rounded focus:outline-none focus:shadow-outline",
  };

  return (
    <section className={className.outerBox}>
      <div className={className.box}>
        <div className={className.innerBox}>
          <form onSubmit={handleRegister} className={className.form}>
            <p className={className.warnText}>{msg}</p>
            <div className={className.formSpacing}>
              <label className={className.formLabel}>Name</label>
              <input
                type="text"
                className={className.formInput}
                placeholder="Name"
                value={name}
                onChange={(e) => setName(e.target.value)}
              />
            </div>
            <div className={className.formSpacing}>
              <label className={className.formLabel}>Email</label>
              <input
                type="text"
                className={className.formInput}
                placeholder="Email"
                value={email}
                onChange={(e) => setEmail(e.target.value)}
              />
            </div>
            <div className={className.formSpacing}>
              <label className={className.formLabel}>Password</label>
              <input
                type="password"
                className={className.formInput}
                placeholder="******"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
              />
            </div>
            <div className={className.formSpacing}>
              <label className={className.formLabel}>Confirm Password</label>
              <input
                type="password"
                className={className.formInput}
                placeholder="******"
                value={confPassword}
                onChange={(e) => setConfPassword(e.target.value)}
              />
            </div>
            <div className={className.submitBox}>
              <button type="submit" className={className.formSubmit}>
                Register
              </button>
            </div>
          </form>
        </div>
      </div>
    </section>
  );
};

export default Register;
