import { Database } from "../Database/WholeData";
import { useViewport } from "./Viewport";

const Navbar = () => {
    const className = {
        container: "w-full absolute top-0 inset-x-0 px-10 z-40 py-4",
        mobile: "w-full flex items-center justify-between gap-10",
        desktop: "w-full grid grid-cols-3 place-items-center gap-10",
        logo: "w-16 h-16 rounded-lg",
        icon: "p-3 rounded-full bg-gray-500/70 text-white group-hover:bg-transparent group-hover:border border-white transform duration-200",
        contactButton: "text-white flex items-center gap-5 group",
        triggerBox: "flex items-center gap-8"
    };

    const viewport = useViewport().windowSize;

    const Data = Database.NavbarData;

    return (
      <div className={className.container}>
        {viewport.innerWidth < 1025 ? (
          <div className={className.mobile}>
            <img src={Data.logo} alt="" className={className.logo} />

            <div className={className.triggerBox}>
              <button className={className.contactButton}>
                <div className={className.icon}>{Data.button.icon}</div>
                <p>{Data.button.title}</p>
              </button>
              <button className={className.sidebarTrigger}>

              </button>
            </div>
          </div>
        ) : (
          <div className={className.desktop}>

          </div>
        )}
      </div>
    );
}


export default Navbar;
